import React from 'react';

require('react-dom');

class Preloader extends React.PureComponent {
  componentDidMount() {
    const {
      images
    } = this.props;
    images.forEach(i => {
      const img = new Image();
      img.src = i;
    });
  }

  render() {
    return null;
  }

}

Preloader.defaultProps = {
  images: []
};
Preloader._idyll = {
  name: 'Preloader',
  tagType: 'closed',
  displayType: 'inline',
  props: [{
    name: 'images',
    type: 'array',
    description: 'the array of images: `["image-url-1.png", "image-url-2.jpg"]`.',
    example: '["image-url-1.png", "image-url-2.jpg"]'
  }]
};

export { Preloader as default };
